var pbutton = document.querySelector(".making__button-order"),
	basket = document.querySelector(".basket"),
	bgmodal = document.querySelector(".bg-modal");
pbutton.addEventListener("click", function (e) {
	e.preventDefault(), basket.classList.remove("basket-none"), bgmodal.classList.remove("bg-modal-none")
}), bgmodal.addEventListener("click", function (e) {
	e.preventDefault(), basket.classList.add("basket-none"), bgmodal.classList.add("bg-modal-none")
}), window.addEventListener("keydown", function (e) {
	27 === e.keyCode && (e.preventDefault(), basket.classList.contains("basket-none") || basket.classList.add("basket-none"))
}), window.addEventListener("keydown", function (e) {
	27 === e.keyCode && (e.preventDefault(), bgmodal.classList.contains("bg-modal-none") || bgmodal.classList.add("bg-modal-none"))
});
